mapper.sql:
		SELECT
		
		FROM org_staff_finance
		WHERE 1=1
		
			and mobile = #{mobile,jdbcType=CHAR}
		
		
		
			and staff_id in 
			
				${item}
			
		

		
			and staff_id in (
			select staff_id from org_staffs where org_id in
			
				${item}
			
			)
		

		
			and is_black = #{isBlack,jdbcType=NUMERIC}
		

		order by add_time desc
