mapper.sql:
select
from org_staff_leave where 1=1
and parent_id = #{parentId,jdbcType=NUMERIC}
and org_id = #{orgId,jdbcType=NUMERIC}
and staff_id = #{staffId,jdbcType=NUMERIC}
and staff_id in (select staff_id from org_staffs where mobile = #{mobile,jdbcType=VARCHAR})
and #{leaveDate,jdbcType=DATE} between leave_date and leave_date_end
and ( #{rangeStartDate,jdbcType=DATE} between leave_date and leave_date_end OR #{rangeEndDate,jdbcType=DATE} between leave_date and leave_date_end )
and leave_status = #{leaveStatus,jdbcType=VARCHAR}
order by add_time desc
