mapper.select:
  	select 
    
    from org_staff_leave
  	where 1=1
  	
  	
		
				and
				(
					(UNIX_TIMESTAMP(leave_date) + start*3600) > #{orderServiceDateStart}
					 and
					(UNIX_TIMESTAMP(leave_date) + start*3600) < #{orderServiceDateEnd}
				)	
				or
				(
					(UNIX_TIMESTAMP(leave_date) + end*3600) > #{orderServiceDateStart}
					 and
					(UNIX_TIMESTAMP(leave_date) + end*3600) < #{orderServiceDateEnd}
				)

  	select 
    
    from org_staff_leave
    where 1=1
    
	    and
		    parent_id = #{cloudOrgId,jdbcType=NUMERIC}
    
    
    
    	and staff_id = #{staffId,jdbcType=NUMERIC}
    
    
    
    	 
    	and 
    		add_time > #{addStartTime,jdbcType=NUMERIC}
    	and	
    		add_time < #{addEndTime,jdbcType=NUMERIC}
		
    
    
  	
    	 
    	and 
    		UNIX_TIMESTAMP(leave_date) =  #{leaveTimeStamp,jdbcType=NUMERIC}
    		
     
    order by add_time desc
