mapper.select:
  	select
  		
  	from 
  		org_staffs
  	where 1=1 
    
		and staff_type = #{staffType}
	
     
		and name like '%${name}%'
	
	
		and sex = #{sex}
	
	
		and mobile like '%${mobile}%' 
	
	
		and am_id like '%${amId}%' 
		
	
		and org_id = #{orgId,jdbcType=NUMERIC}
	
	
		and parent_org_id = #{parentId,jdbcType=NUMERIC}
	
	
	order by add_time desc

  		select
  		
  		from org_staffs
 		where 1=1 
 		
 			and org_id = #{orgId}
 		
	    
			and staff_type = #{staffType}
