mapper.sql:
		from order_dispatchs
		where 1=1

		
			and staff_id =#{staffId,jdbcType=NUMERIC}
		

		
			and staff_id in
			
				#{item}
			
		

		
			and staff_mobile =#{staffMobile,jdbcType=VARCHAR}
		

		
			and staff_name =#{staffName,jdbcType=VARCHAR}
		

		
			and user_id = #{userId,jdbcType=NUMERIC}
		

		
			and mobile = #{mobile}
		

		
			and order_id = #{orderId,jdbcType=NUMERIC}
		

		
			and order_id in
			
				#{item}
			
		

		
			and order_no = #{orderNo,jdbcType=VARCHAR}
		

		
			and org_id = #{orgId,jdbcType=NUMERIC}
		

		
			and parent_id = #{parentId,jdbcType=NUMERIC}
		

		
			and is_apply = #{isApply,jdbcType=NUMERIC}
		

		
			and org_id in
			
				#{item}
			
		

		
			and parent_id in
			
				#{item}
			
		

		
			 and service_date >= #{startServiceTime}
		

		
			 and service_date <= #{endServiceTime}
		

		
			 and (service_date + service_hours *3600) >= #{startServiceHourTime}
		

		
			 and (service_date + service_hours *3600) <= #{endServiceHourTime}
		

		
			 and add_time >= #{startAddTime}
		

		
			 and add_time <= #{endAddTime}
		

		
			 and update_time >= #{startUpdateTime}
		

		
			 and update_time <= #{endUpdateTime}
		


		
			and dispatch_status = #{dispatchStatus,jdbcType=NUMERIC}
